package boggle;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Random;
import java.util.Set;

import tree.LexicographicTree;
import org.jgrapht.graph.SimpleGraph;
import org.jgrapht.Graph;
import org.jgrapht.Graphs;
import org.jgrapht.graph.DefaultEdge;
public class Boggle {	
	private Graph<String, DefaultEdge> boggleGraph;
	private Map<String, Integer> indexVertex;
	private LexicographicTree dict;
	private String letters;
	private int size;
	
	/*
	 * CONSTRUCTORS
	 */
	
	/**
	 * Constructor : creates a Boggle grid filled with random letters.
	 * @param size The size of the squared grid
	 * @param dict A dictionary of allowed words
	 * @throws Exception 
	 */
	public Boggle(int size, LexicographicTree dict){
		// TODO
		if(size < 1) {
			throw new IllegalArgumentException("La taille de la grille ne peut pas être < 1");
		}
	
		this.dict = dict;
		this.size = size;		    
	    this.letters = generateLetters();
	    
	    createBoggle();
	}
	

	
	/**
	 * Constructor : creates a Boggle grid filled with the supplied letters.
	 * @param size The size of the squared grid
	 * @param letters A string containing the (size x size) letters used to fill the grid
	 * @param dict A dictionary of allowed words
	 */
	public Boggle(int size, String letters, LexicographicTree dict) {
		// TODO
		if(size < 1 || size*size > letters.length()) {
			throw new IllegalArgumentException("Argument(s) du constructeur invalide(nt)");
		}
				
		this.dict = dict;
		this.size = size;
		this.letters = letters;
		
		createBoggle();
	}
	
	/*
	 * PUBLIC METHODS
	 */
	
	/**
	 * Returns the letters in the Boggle grid.
	 * @return a string of letters
	 */
	public String letters() {
		// TODO
		return this.letters; 
	}
	
	/**
	 * Determines if a word can be found in the Boggle grid.
	 * @param word a word
	 * @return true if the word is present, false otherwise
	 */
	public boolean contains(String word) {
		// TODO	
	    if (word.isEmpty()) {
	        return false;
	    }
	    
	    List<String> visited = new ArrayList<>();
	    for (String vertex : boggleGraph.vertexSet()) {
            if (word.charAt(0) == vertex.charAt(0)) {
                if (findWordInBoggle(word, vertex, 0, visited)) {
                    return true;
                }
            }
        }
        return false;
	}
	

	/**
	 * Searches for words in the Boggle grid.
	 * @return the set of found words
	 */
	public Set<String> solve() {
		// TODO			
		Set<String> wordsFound = new HashSet<>();
        boolean[] visited = new boolean[size*size];
        StringBuilder currentWord = new StringBuilder();
    
    	for (String vertex : boggleGraph.vertexSet()) {
            explore(boggleGraph, visited, vertex, currentWord, wordsFound);
        }
        
        return wordsFound;		
	}
	
	/**
	 * Returns a textual representation of the Boggle grid.
	 * @return a textual representation of the Boggle grid
	 */
	public String toString() {
		// TODO
		StringBuilder sb = new StringBuilder();
		int cpt = 1;
		
		for (String vertex : boggleGraph.vertexSet()) {
		    sb.append(vertex.charAt(0));
		    sb.append(" ");
		    
		    if(cpt == size) {
		        sb.append("\n");
		        cpt = 0;
		    }
		    
		    cpt++;
		}
		
		return sb.toString();
	}
	

	/*
	 * PRIVATE METHODS
	 */
	// TODO	
	
	//CREATION
	/**
	 * Méthode qui permet de créer une grille de boggle sur base 
	 * d'une série de lettres et d'une taille
	 */
	private void createBoggle() {		
	    this.boggleGraph = new SimpleGraph<>(DefaultEdge.class);
	    this.indexVertex = new HashMap<String, Integer>();
	    int cpt = 0;
	        
	    // création des sommets
	    for (int i = 0; i < size; i++) {
	        for (int j = 0; j < size; j++) {
	            String vertex = String.valueOf(this.letters.charAt(cpt)) + "_" + i + "_" + j;
	            boggleGraph.addVertex(vertex); 
	            this.indexVertex.put(vertex, cpt);
	            
	            cpt++;
	        }
	    }
	        
	    cpt = 0;
	    // ajout des arêtes
	    for (int i = 0; i < size; i++) {
	        for (int j = 0; j < size; j++) {
	            String vertex1 = String.valueOf(this.letters.charAt(cpt)) + "_" + i + "_" + j;
	            cpt++;
	                
	            // ajouter les arêtes en vérifiants les sommets adjacents
	            for (int k = i - 1; k <= i + 1; k++) {
	                for (int l = j - 1; l <= j + 1; l++) {
	                	//Vérification des bornes
	                    if (k >= 0 && k < size && l >= 0 && l < size && (k != i || l != j)) {
	                        String vertex2 = String.valueOf(this.letters.charAt(k * size + l)) + "_" + k + "_" + l;
	                        boggleGraph.addEdge(vertex1, vertex2);
	                    }
	                }
	            }
	        }
	    }
	}
	
	
	/**
	 * Méthode qui permet de chercher tous les mots possibles à partir d'un sommet
	 * @param boggleGraph Le graphe dans lequel on doit chercher
	 * @param visited Un tableau indiquant les lettres déjà visitées ou non
	 * @param currentVertex Le vertex sur base duquel on fait la recherche
	 * @param currentWord La valeur de la séquence de lettre actuelle 
	 * @param wordsFound L'ensemble des mots trouvés dans la grille qui sont présents dans le dictionnaire
	 */
    private void explore(Graph<String, DefaultEdge> boggleGraph, boolean[] visited, String currentVertex,StringBuilder currentWord, Set<String> wordsFound) { 	
		  	visited[this.indexVertex.get(currentVertex)] = true;
		    StringBuilder nextFragment = new StringBuilder(currentWord).append(currentVertex.charAt(0));
	
		    if (nextFragment.length() > 2 && this.dict.containsWord(nextFragment.toString())) {
		        wordsFound.add(nextFragment.toString());
		    }
	
		    if(dict.existPrefix(nextFragment.toString())) {
		        for (String neighborVertex : Graphs.neighborListOf(boggleGraph, currentVertex)) {
		            if (!visited[this.indexVertex.get(neighborVertex)]) {
		                explore(boggleGraph, visited, neighborVertex, nextFragment, wordsFound);
		            }            
		       }
		    }
		    
		    visited[this.indexVertex.get(currentVertex)] = false;     
    }

    /**
     * Méthode qui permet de générer 26 lettres de manières aléatoire
     * @return Une séquence de 26 lettres 
     */
    private String generateLetters() {
    	Random rand = new Random();
    	char[] letters = new char[size * size];
    	
    	for (int i = 0; i < size * size; i++) {
    		letters[i] = (char) (rand.nextInt(26) + 'a');
    	}
    	
    	return new String(letters);
    }
    
    /**
     * Méthode qui permet de savoir si un mot est trouvable dans la grille
     * @param word Le mot que l'on veut trouver dans la grille
     * @param vertex Le sommet courant
     * @param index L'index courant
     * @param visited La liste des sommets déjà visités
     * @return True si le mot peut être trouvé dans la grille sinon false
     */
    private boolean findWordInBoggle(String word, String vertex, int index, List<String> visited) {
		 if (index == word.length()-1) {
	            return true;
	        }
		 
	        visited.add(vertex);
	        for (DefaultEdge edge : boggleGraph.edgesOf(vertex)) {
	            String nextVertex = boggleGraph.getEdgeTarget(edge);
	            
	            if (nextVertex.equals(vertex)) {
	                nextVertex = boggleGraph.getEdgeSource(edge);
	            }
	            
	            if (!visited.contains(nextVertex) && word.charAt(index + 1) == nextVertex.charAt(0)) {
	                if (findWordInBoggle(word, nextVertex, index + 1, visited)) {
	                    return true;
	                }
	            }
	        }
	        
	        visited.remove(vertex);
	        return false;
   }
    
	/*
	 * MAIN PROGRAM
	 */
	
	public static void main(String[] args) {
		long startTime = System.currentTimeMillis();
		System.out.println("Loading dictionary...");
		LexicographicTree dictionary = new LexicographicTree("mots/dictionnaire_FR_sans_accents.txt");
		long loadDictTime = System.currentTimeMillis();
		System.out.println("Duration : " + (loadDictTime - startTime)/1000.0);
		System.out.println("Number of words : " + dictionary.size());
		System.out.println();

		Boggle[] tabBoggle = new Boggle[6];
		
		// Sample grids
		String grid4x4 = "rhreypcswnsntego"; // Wikipedia example
		Boggle boggle1 = new Boggle(4, grid4x4, dictionary);
		tabBoggle[0] = boggle1;

		String grid10x10 = "eymmccsrltjttsdiraoarliuniepeousrcgoiseerreeistiedtomcteevcmkaualilaretneerectresieenspgizeoeceecuds";
		Boggle boggle2 = new Boggle(10, grid10x10, dictionary);
		tabBoggle[1] = boggle2;
		
		String grid20x20 = "";
		Boggle boggle3 = new Boggle(20, grid20x20, dictionary);
		tabBoggle[2] = boggle3;
		
		String grid50x50 = "";
		Boggle boggle4 = new Boggle(50, grid50x50, dictionary);
		tabBoggle[3] = boggle4;
		
		String grid100x100 = "";
		Boggle boggle5 = new Boggle(100, grid100x100, dictionary);
		tabBoggle[4] = boggle5;
		
		String grid150x150 = "";
		Boggle boggle6 = new Boggle(150, grid150x150, dictionary);
		tabBoggle[5] = boggle6;
	
		for(Boggle boggle : tabBoggle) {
			System.out.println("Boggle grid :");
			System.out.println(boggle.toString());

			// Solve grid
			System.out.println("Solving Boggle grid...");
			Set<String> results = boggle.solve();
			long solveTime = System.currentTimeMillis();
			System.out.println("Duration : " + (solveTime - loadDictTime)/1000.0);
			System.out.println("Number of words found : " + results.size());
//			System.out.println(new TreeSet<String>(results).toString());
			
			System.out.println("------------------- \n");
		}
	}
}
